import { motion } from 'framer-motion'
import { Link } from 'react-scroll'

const NavLinks = (props) => {
    const animationFrom = { opacity: 0, y: -40 }
    const animateTo = { opacity: 1, y: 0 }

    return (
        <ul className="menu">
            <motion.li className='nav-li' initial={animationFrom} animate={animateTo} transition={{ delay: 0.05 }} onClick={() => props.isMobile && props.closeMobileMenu()}><Link href="home" className="nav-link">Home</Link></motion.li>
            <li className='nav-li'>
                <Link
                    activeClass="active"
                    to="about"
                    spy={true}
                    smooth={true}
                    offset={-100}
                    duration={500}

                    className="nav-link">About</Link></li>
            <motion.li className='nav-li' initial={animationFrom} animate={animateTo} transition={{ delay: 0.15 }} onClick={() => props.isMobile && props.closeMobileMenu()}><a href="#skills" className="nav-link">Skills</a></motion.li>
            <motion.li className='nav-li' initial={animationFrom} animate={animateTo} transition={{ delay: 0.20 }} onClick={() => props.isMobile && props.closeMobileMenu()}><a href="" className="nav-link">CV</a></motion.li>
            <motion.li className='nav-li' initial={animationFrom} animate={animateTo} transition={{ delay: 0.25 }} onClick={() => props.isMobile && props.closeMobileMenu()}><a href="" className="nav-link">Contact</a></motion.li>
        </ul>
    );
}

export default NavLinks;
